async function swapETHforExactTokens() {
    let amount = web3.utils.toWei(amountETH, "ether");
    const block = web3.eth.getBlock('latest')
    block.then(console.log);

    let gas = await swap.methods.getAmountsIn(amount, [wrappedAddr,coin]).call({from:accounts[0]});
    if(networkId == 43114) {
      await swap.methods.swapAVAXForExactTokens(amount, [wrappedAddr, coin], accounts[0], deadline).send({from:accounts[0], value: gas[0]});
     } else {
      await swap.methods.swapETHForExactTokens(amount, [wrappedAddr, coin], accounts[0], deadline).send({from:accounts[0], value: gas[0]});

     }
}

